import React, {useState, useEffect, useContext} from 'react'
import { v4 as uuidv4 } from 'uuid';
import {AppContext} from './Context'

export default function Pagination() {

    const [person, setPerson, personFinal, setPersonFinal, fname, setFname,lname, setLname,age,setAge,
        addNewPerson, editPerson, deletePesron, saveToggle, cancelUpdate, cancelBtn,
        itemsPerPage, setItemsPerPage,numPages, setNumPages, currPageNum, setCurrpageNum]=useContext(AppContext)

const prevBtnHandler=()=>{
    alert(itemsPerPage)
    currPageNum <= 0 ? setCurrpageNum(0) : setCurrpageNum(currPageNum--)
    setPerson(person.slice(currPageNum * 3 , 3))

}

const nextBtnHandler=()=>{
    alert(numPages)
    currPageNum == numPages ? setCurrpageNum(1) : setCurrpageNum(2)
    setPerson(person.slice(currPageNum * 3 , 3))

}

    return (
        <div>
            <input type="button" onClick={()=>prevBtnHandler()} value="Prev" width="10px" height="10px"/>
            <input type="button" onClick="" value={`Current Page : ${currPageNum} ` }/>
            <input type="button" onClick={()=>nextBtnHandler()} value="Next" />
              {numPages}
        </div>

    )
}
